void dfs1(int u, int f, int deep)
{
    dep[u] = deep;   //标记深度
    fa[u] = f;       //标记节点的父亲
    siz[u] = 1;      //记录每个节点子树大小
    int maxson = -1; //记录重儿子数量
    for (int i = first[u]; ~i; i = e[i].next)
    {
        int v = e[i].v;
        if (v == f)
            continue;
        dfs1(v, u, deep + 1);
        siz[u] += siz[v];
        if (siz[v] > maxson) //儿子里最多siz就是重儿子
        {
            son[u] = v; //标记u的重儿子为v
            maxson = siz[v];
        }
    }
}

