    dfs2(son[u], topf); //搜索下一个重儿子
    for (int i = first[u]; ~i; i = e[i].next)
    {
        int v = e[i].v;
        if (v == fa[u] || v == son[u]) //处理轻儿子
            continue;
        dfs2(v, v); //每一个轻儿子都有一个从自己开始的链
    }
}
